import React, { useEffect } from "react"
import { gsap } from "gsap"

export default function GsapTest() {
  const header = React.createRef()

  useEffect(() => {
    gsap.to(header.current, { color: "navy", duration: 2 })
  }, [header])

  return (
    <div>
      <h1 ref={header}>hellow Gsap world!</h1>
    </div>
  )
}
